import { App } from 'aws-cdk-lib';
import { Template } from 'aws-cdk-lib/assertions';
import { InstanceSize } from 'aws-cdk-lib/aws-ec2';
import { DotcomRenderingHalloween } from './dotcom-rendering-halloween';

/**
 * These tests make sure that the cloudformation template being generated by CDK has not deviated
 * from the cloudformation snapshot that has been checked into our repository.
 */
describe('The DotcomRendering stack', () => {
	it('matches the snapshot', () => {
		const app = new App();
		const stack = new DotcomRenderingHalloween(app, 'DotcomRendering', {
			stack: 'frontend',
			stage: 'TEST',
			app: 'rendering',
			instanceSize: InstanceSize.MICRO,
			scaling: { minimumInstances: 1, maximumInstances: 4 },
		});
		const template = Template.fromStack(stack);
		expect(template.toJSON()).toMatchSnapshot();
	});
});
